
  $(document).ready(function () {
    var t = ["blueish", "brownish", "purpleish", "rediesh", ""],
      e = [
       
        "A Social",
        "An Artistic",
        "A Global",
        "A Lazy",
        "A Profitable",
        "A community",
         "A Fun",
      ],
      n = [
        "zombie",
        "beard",
        "gold",
        "og",
        "orange",
        "robot",
         
      ],
      a = 0,
      o = 0;
    $("#change-Sloth").click(function () {
      $("html").each(function () {
        this.className = t[($.inArray(this.className, t) + 1) % t.length];
      }),
        $("#change-word").text(e[a]),
        a++ < e.length - 1 || (a = 0),
        $("#change-Sloth img").attr(
          "src",
          "imgs/Races/"+n[o]+".webp"
        ),
        o++ < n.length - 1 || (o = 0);
    }),
      "/" == window.location.pathname &&
        (window.onload = function () {
          for (var t = 0; t < n.length; t++) {
            var e = new Image();
            e.src = "/assets/buildabruh/fur-" + n[t] + ".png";
          }
        });
  }),
  $(document).ready(function () {
    $(".question").click(function () {
      $(this).next(".answer").slideToggle(150),
        $(this).children("i").toggleClass("bx-rotate-90");
    }),
      $(".scorp").click(function () {
        $("body").toggleClass("dark");
      }),
      $(".download-png")
        .attr({ tooltip: "Download PNG", "tooltip-position": "right" })
        .click(function () {
          var t = $(this).parent().attr("id");
          $(this).attr("href", "/assets/honorary/honorary-" + t + ".png");
        }),
      $(".gallery-item .twitter-link").attr({
        tooltip: "View Tweet",
        "tooltip-position": "bottom",
      }),
      $(".gallery-item").click(function () {
        $("html").hasClass($(this).attr("id"))
          ? $("html").removeClass()
          : ($("html").removeClass(), $("html").addClass($(this).attr("id")));
      });
  });
